pragma solidity >=0.8.0 <0.9.0;

import "./callback/TokenCallbackHandler.sol";
import "@openzeppelin/contracts/metatx/ERC2771Context.sol";
import "@openzeppelin/contracts/proxy/utils/Initializable.sol";
import "./base/PasskeyManager.sol";
import "./base/RecoveryManager.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@chainlink/contracts/src/v0.8/interfaces/AggregatorV3Interface.sol";
import "@openzeppelin/contracts/metatx/ERC2771Context.sol";


contract Valerium is TokenCallbackHandler, Initializable, PasskeyManager, RecoveryManager, ERC2771Context {
    address public VALERIUM_FORWARDER;
    address public VALERIUM_GAS_TANK;
    address public GHO_TOKEN;
    address public GHO_AGGREGATOR;
    address public ETH_AGGREGATOR;

    constructor(address _forwarder, address _gasTank, address _ghoToken, address _ghoAggregator, address _ethAggregator) ERC2771Context(_forwarder) {
        VALERIUM_FORWARDER = _forwarder;
        VALERIUM_GAS_TANK = _gasTank;
        GHO_TOKEN = _ghoToken;
        GHO_AGGREGATOR = _ghoAggregator;
        ETH_AGGREGATOR = _ethAggregator;
    }

    modifier onlyValeriumForwarder() {
        require(msg.sender == VALERIUM_FORWARDER, "Only Valerium Forwarder can call this function");
        _;
    }

    modifier notValeriumForwarder() {
        require(msg.sender != VALERIUM_FORWARDER, "Valerium Forwarder cannot call this function");
        _;
    }

    function initialize(address _passkeyVerifier, bytes memory _passkeyInputs, address _recoveryVerifier, bytes32 _recoveryKeyHash) public virtual initializer {
        _initialize(_passkeyVerifier, _passkeyInputs, _recoveryVerifier, _recoveryKeyHash);
    }


    function _initialize(address _passkeyVerifier, bytes memory _passkeyInputs, address _recoveryVerifier, bytes32 _recoveryKeyHash) internal virtual {
        _initializePasskeyManager(_passkeyVerifier, _passkeyInputs);
        _initializeRecoveryManager(_recoveryVerifier, _recoveryKeyHash);
    }

    function execute(bytes calldata proof, address dest, uint256 value, bytes calldata func) public payable notValeriumForwarder returns (bool) {
        require(usePasskey(proof), "Invalid passkey");
        _execute(dest, value, func);
        return true;
    }

    function executeBatch(bytes calldata proof, address[] calldata dest, uint256[] calldata value, bytes[] calldata func) public payable notValeriumForwarder returns (bool) {
        require(usePasskey(proof), "Invalid passkey");
        _executeBatch(dest, value, func);
        return true;
    }

    function executeRecovery(bytes calldata proof, bytes memory _passkeyInputs) public payable notValeriumForwarder returns (bool) {
        require(useRecovery(proof), "Invalid recovery");
        changePasskeyInputs(_passkeyInputs);
        return true;
    }

    function executeNative(bytes calldata proof, address dest, uint256 value, bytes calldata func, uint256 baseFees, uint256 expectedFees) public payable onlyValeriumForwarder returns (bool) {
        require(address(this).balance >= expectedFees + value, "Not enough fees");

        uint256 gas = gasleft();
        execute(proof, dest, value, func);
        uint256 gasUsed = gas - gasleft();
        uint256 fees = (gasUsed * tx.gasprice) + baseFees;

        payable(VALERIUM_GAS_TANK).transfer(fees);
        return true;
    }

    function executeBatchNative(bytes calldata proof, address[] calldata dest, uint256[] calldata value, bytes[] calldata func, uint256 baseFees, uint256 expectedFees) public payable onlyValeriumForwarder returns (bool) {
        uint256 totalValue = 0;
        for (uint256 i = 0; i < value.length; i++) {
            totalValue += value[i];
        }
        require(address(this).balance >= expectedFees + totalValue, "Not enough fees");

        uint256 gas = gasleft();
        executeBatch(proof, dest, value, func);
        uint256 gasUsed = gas - gasleft();
        uint256 fees = (gasUsed * tx.gasprice) + baseFees;

        payable(VALERIUM_GAS_TANK).transfer(fees);
        return true;
    }

    function executeRecoveryNative(bytes calldata proof,  bytes memory _passkeyInputs, uint256 baseFees, uint256 expectedFees) public payable onlyValeriumForwarder returns (bool) {
        require(address(this).balance >= expectedFees, "Not enough fees");

        uint256 gas = gasleft();
        executeRecovery(proof, _passkeyInputs);
        uint256 gasUsed = gas - gasleft();
        uint256 fees = (gasUsed * tx.gasprice) + baseFees;

        payable(VALERIUM_GAS_TANK).transfer(fees);
        return true;
    }

    function getGHOAmount(int256 fees) public view returns (int256) {
        AggregatorV3Interface ethAggregator = AggregatorV3Interface(ETH_AGGREGATOR);
        AggregatorV3Interface ghoAggregator = AggregatorV3Interface(GHO_AGGREGATOR);

        (, int256 ethPrice, , , ) = ethAggregator.latestRoundData();
        (, int256 ghoPrice, , , ) = ghoAggregator.latestRoundData();

        int256 ratio = ethPrice / ghoPrice;

        int256 feeRatio = fees * ratio;
        return feeRatio;
    }

    function executePayGHO(bytes calldata proof, address dest, uint256 value, bytes calldata func, uint256 baseFees, uint256 expectedFees) public payable onlyValeriumForwarder returns (bool) {
        require(IERC20(GHO_TOKEN).balanceOf(address(this)) >= uint(getGHOAmount(int(expectedFees))), "Not enough fees");

        uint256 gas = gasleft();
        execute(proof, dest, value, func);
        uint256 gasUsed = gas - gasleft();
        uint256 fees = (gasUsed * tx.gasprice) + baseFees;

        uint256 ghoAmount = uint(getGHOAmount(int(fees)));
        IERC20(GHO_TOKEN).transfer(VALERIUM_GAS_TANK, ghoAmount);
        return true;
    }

    function executeBatchPayGHO(bytes calldata proof, address[] calldata dest, uint256[] calldata value, bytes[] calldata func, uint256 baseFees, uint256 expectedFees) public payable onlyValeriumForwarder returns (bool) {
        require(IERC20(GHO_TOKEN).balanceOf(address(this)) >= uint(getGHOAmount(int(expectedFees))), "Not enough fees");

        uint256 gas = gasleft();
        executeBatch(proof, dest, value, func);
        uint256 gasUsed = gas - gasleft();
        uint256 fees = (gasUsed * tx.gasprice) + baseFees;

        uint256 ghoAmount = uint(getGHOAmount(int(fees)));
        IERC20(GHO_TOKEN).transfer(VALERIUM_GAS_TANK, ghoAmount);
        return true;
    }

    function executeRecoveryPayGHO(bytes calldata proof, bytes memory _passkeyInputs, uint256 baseFees, uint256 expectedFees) public payable onlyValeriumForwarder returns (bool) {
        require(IERC20(GHO_TOKEN).balanceOf(address(this)) >= uint(getGHOAmount(int(expectedFees))), "Not enough fees");

        uint256 gas = gasleft();
        executeRecovery(proof, _passkeyInputs);
        uint256 gasUsed = gas - gasleft();
        uint256 fees = (gasUsed * tx.gasprice) + baseFees;

        uint256 ghoAmount = uint(getGHOAmount(int(fees)));
        IERC20(GHO_TOKEN).transfer(VALERIUM_GAS_TANK, ghoAmount);
        return true;
    }

    function _execute(address dest, uint256 value, bytes calldata func) internal {
        _call(dest, value, func);
    }

    function _executeBatch(address[] calldata dest, uint256[] calldata value, bytes[] calldata func) internal {
        require(dest.length == func.length && (value.length == 0 || value.length == func.length), "wrong array lengths");
        if (value.length == 0) {
            for (uint256 i = 0; i < dest.length; i++) {
                _call(dest[i], 0, func[i]);
            }
        } else {
            for (uint256 i = 0; i < dest.length; i++) {
                _call(dest[i], value[i], func[i]);
            }
        }
    }

	function _call(address target, uint256 value, bytes memory data) internal {
        (bool success, bytes memory result) = target.call{value : value}(data);
        if (!success) {
            assembly {
                revert(add(result, 32), mload(result))
            }
        }
    }

	receive() external payable {}

    fallback() external payable {}
}","keccak256":"0x2258084e6974d710ad1293b5339aad4071418eab61935729a12b476281e0a3fa","license":"MIT"},"contracts/ValeriumFactory.sol":{"content":"//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "@openzeppelin/contracts/utils/Create2.sol";
import "@openzeppelin/contracts/proxy/ERC1967/ERC1967Proxy.sol";
import "@openzeppelin/contracts/metatx/ERC2771Context.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import {IRouterClient} from "@chainlink/contracts-ccip/src/v0.8/ccip/interfaces/IRouterClient.sol";
import {OwnerIsCreator} from "@chainlink/contracts-ccip/src/v0.8/shared/access/OwnerIsCreator.sol";
import {Client} from "@chainlink/contracts-ccip/src/v0.8/ccip/libraries/Client.sol";
import {CCIPReceiver} from "@chainlink/contracts-ccip/src/v0.8/ccip/applications/CCIPReceiver.sol";

import "./ValeriumStorage.sol";
import "./Valerium.sol";

contract ValeriumFactory is ValeriumStorage, ERC2771Context, OwnerIsCreator, CCIPReceiver{

    error NotEnoughBalance(uint256 currentBalance, uint256 calculatedFees);
    error NothingToWithdraw(); 
    error FailedToWithdrawEth(address owner, address target, uint256 value); 
    error DestinationChainNotAllowlisted(uint64 destinationChainSelector); 
    error SourceChainNotAllowlisted(uint64 sourceChainSelector); 
    error SenderNotAllowlisted(address sender); 

    event ValeriumCreated(string name, address valerium);

    Valerium public immutable accountImplementation;

    IERC20 private s_linkToken;

    mapping(uint64 => bool) public allowlistedDestinationChains;

    mapping(uint64 => bool) public allowlistedSourceChains;

    mapping(address => bool) public allowlistedSenders;

    constructor(address _router, 
        address _link, 
        address _trustedForwarder, 
        address _gasTank, 
        address _ghoToken, 
        address _ghoAggregator, 
        address _ethAggregator) ERC2771Context(_trustedForwarder) CCIPReceiver(_router) {
        accountImplementation = new Valerium(_trustedForwarder, _gasTank, _ghoToken, _ghoAggregator, _ethAggregator);
        s_linkToken = IERC20(_link);
    }

     modifier onlyTrustedForwarder() {
        require(isTrustedForwarder(msg.sender), "caller is not the trusted forwarder");
        _;
    }

    modifier onlyAllowlistedDestinationChain(uint64 _destinationChainSelector) {
        if (!allowlistedDestinationChains[_destinationChainSelector])
            revert DestinationChainNotAllowlisted(_destinationChainSelector);
        _;
    }

    modifier onlyAllowlisted(uint64 _sourceChainSelector, address _sender) {
        if (!allowlistedSourceChains[_sourceChainSelector])
            revert SourceChainNotAllowlisted(_sourceChainSelector);
        if (!allowlistedSenders[_sender]) revert SenderNotAllowlisted(_sender);
        _;
    }

    function allowlistDestinationChain(
        uint64 _destinationChainSelector,
        bool allowed
    ) external onlyOwner {
        allowlistedDestinationChains[_destinationChainSelector] = allowed;
    }

    function allowlistSourceChain(
        uint64 _sourceChainSelector,
        bool allowed
    ) external onlyOwner {
        allowlistedSourceChains[_sourceChainSelector] = allowed;
    }

    function allowlistSender(address _sender, bool allowed) external onlyOwner {
        allowlistedSenders[_sender] = allowed;
    }

    function sendMessagePayLINK(
        uint64 _destinationChainSelector,
        address _receiver,
        string memory name, 
        address _passkeyVerifier, 
        bytes memory _passkeyInputs, 
        address _recoveryVerifier, 
        bytes32 _recoveryKeyHash, 
        uint256 salt
    )
        internal
        onlyAllowlistedDestinationChain(_destinationChainSelector)
        returns (bytes32 messageId)
    {

        Client.EVM2AnyMessage memory evm2AnyMessage = _buildCCIPMessage(
            _receiver,
            name,
            _passkeyVerifier,
            _passkeyInputs,
            _recoveryVerifier,
            _recoveryKeyHash,
            salt,
            address(s_linkToken)
        );

        IRouterClient router = IRouterClient(this.getRouter());

        uint256 fees = router.getFee(_destinationChainSelector, evm2AnyMessage);

        if (fees > s_linkToken.balanceOf(address(this)))
            revert NotEnoughBalance(s_linkToken.balanceOf(address(this)), fees);

        s_linkToken.approve(address(router), fees);

        messageId = router.ccipSend(_destinationChainSelector, evm2AnyMessage);

        return messageId;
    }

    function _buildCCIPMessage(
        address _receiver,
        string memory name, 
        address _passkeyVerifier, 
        bytes memory _passkeyInputs, 
        address _recoveryVerifier, 
        bytes32 _recoveryKeyHash, 
        uint256 salt,
        address _feeTokenAddress
    ) internal pure returns (Client.EVM2AnyMessage memory) {
        return
            Client.EVM2AnyMessage({
                receiver: abi.encode(_receiver),
                data: abi.encode(name,_passkeyVerifier,_passkeyInputs, _recoveryVerifier,_recoveryKeyHash, salt),
                tokenAmounts: new Client.EVMTokenAmount[](0),
                extraArgs: Client._argsToBytes(
                    Client.EVMExtraArgsV1({gasLimit: 900_000})
                ),
                feeToken: _feeTokenAddress
            });
    }

    function _ccipReceive(
        Client.Any2EVMMessage memory any2EvmMessage
    )
        internal
        override
        onlyAllowlisted(
            any2EvmMessage.sourceChainSelector,
            abi.decode(any2EvmMessage.sender, (address))
        ) 
    {
        (
            string memory name, 
            address _passkeyVerifier, 
            bytes memory _passkeyInputs, 
            address _recoveryVerifier, 
            bytes32 _recoveryKeyHash, 
            uint256 salt
        ) = abi.decode(any2EvmMessage.data, (string, address, bytes, address, bytes32, uint256));

        Valerium valerium = createAccount(name, _passkeyVerifier, _passkeyInputs, _recoveryVerifier, _recoveryKeyHash, salt);

        emit ValeriumCreated(name, address(valerium));
    }

    function createAccount(
        string memory name, 
        address _passkeyVerifier, 
        bytes memory _passkeyInputs, 
        address _recoveryVerifier, 
        bytes32 _recoveryKeyHash, 
        uint256 salt) public returns (Valerium ret) {
        address addr = getAddress(_passkeyVerifier, _passkeyInputs, _recoveryVerifier, _recoveryKeyHash, salt);
        uint codeSize = addr.code.length;
        if (codeSize > 0) {
            return Valerium(payable(addr));
        }
        ret = Valerium(payable(new ERC1967Proxy{salt : bytes32(salt)}(
                address(accountImplementation),
                abi.encodeCall(Valerium.initialize, (_passkeyVerifier, _passkeyInputs, _recoveryVerifier, _recoveryKeyHash))
            )));
        addValerium(name, address(ret));
    }

    function createExternalAccount(
        uint64 _destinationChainSelector, 
        address _receiver,
        string memory name, 
        address _passkeyVerifier, 
        bytes memory _passkeyInputs, 
        address _recoveryVerifier, 
        bytes32 _recoveryKeyHash, 
        uint256 salt) public returns (Valerium ret) {

        sendMessagePayLINK(_destinationChainSelector, _receiver, name, _passkeyVerifier, _passkeyInputs, _recoveryVerifier, _recoveryKeyHash, salt);

        emit ValeriumCreated(name, address(ret));
    }

    function getAddress( address _passkeyVerifier, 
    bytes memory _passkeyInputs, 
    address _recoveryVerifier, 
    bytes32 _recoveryKeyHash,
    uint256 salt) public view returns (address) {
        return Create2.computeAddress(bytes32(salt), keccak256(abi.encodePacked(
            type(ERC1967Proxy).creationCode,
            abi.encode(
                address(accountImplementation),
                abi.encodeCall(Valerium.initialize, (_passkeyVerifier, _passkeyInputs, _recoveryVerifier, _recoveryKeyHash))
            )
        )));
    }
}","keccak256":"0x8eb1f5d850329c9afa3d9e70e070276f64855b23e103194a37c5868f62059628","license":"MIT"},"contracts/ValeriumStorage.sol":{"content":"//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

contract ValeriumStorage {

    struct ValeriumDetails {
        address walletAddress;
        bool isUsed;
    }

    mapping(string => ValeriumDetails) public ValeriumNameToDetails;

    function addValerium(string memory name, address walletAddress) internal {
        ValeriumNameToDetails[name] = ValeriumDetails(walletAddress, true);
    }

    function _checkValerium(string memory name) internal view {
        require(ValeriumNameToDetails[name].isUsed, "Valerium: Invalid Valerium");
    }

    function getValerium(string memory name) external view returns (ValeriumDetails memory) {
        ValeriumDetails memory details = ValeriumNameToDetails[name];
        return details;
    }

    modifier isValidValerium(string memory name) {
        _checkValerium(name);
        _;
    }

