pragma solidity >=0.8.0 <0.9.0;

import "../verifier/PasskeyVerifier.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "../util/Conversion.sol";

contract PasskeyManager {
    UltraVerifier public passkeyVerifier;

    bytes private inputs;

    uint256 private passkeyNonce;

    error InvalidAuthenticatorData();
    error InvalidClientData();

    function _initializePasskeyManager(address _passkeyVerifier, bytes memory _passkeyInputs) internal {
        passkeyVerifier = UltraVerifier(_passkeyVerifier);
        inputs = _passkeyInputs;
    }

    function getPasskeyNonce() public view returns (string memory) {
        return Strings.toString(passkeyNonce);
    }

    function _usePasskeyNonce() internal returns (string memory) {
        passkeyNonce++;
        return Strings.toString(passkeyNonce);
    }

    function getCredentialId() public view returns (string memory) {
        (,string memory credentialId, , , , ) = decodeEncodedInputs(inputs);
        return credentialId;
    }

    function changePasskeyVerifier(address _passkeyVerifier) internal {
        passkeyVerifier = UltraVerifier(_passkeyVerifier);
    }

    function changePasskeyInputs(bytes memory _passkeyInputs) internal {
        inputs = _passkeyInputs;
    }

    function verifyPasskey(bytes calldata proof) public view returns (bool) {

        bytes32 message = getMessage();
        (
            bytes32 pubkeyHash,,,,,
        ) = decodeEncodedInputs(inputs);

        bytes32[] memory _inputs = Conversion.convertInputs(message, pubkeyHash);

        return passkeyVerifier.verify(proof, _inputs);
    }

    function usePasskey(bytes calldata proof) internal returns (bool) {
        require(verifyPasskey(proof), "Invalid passkey");
        _usePasskeyNonce();
        return true;
    }

    function getMessage() public view returns (bytes32) {
        (
            ,
            ,
            bytes memory authenticatorData,
            bytes1 authenticatorDataFlagMask,
            bytes memory clientData,
            uint clientChallengeDataOffset
        ) = decodeEncodedInputs(inputs);

        return
            computeMessage(
                authenticatorData,
                authenticatorDataFlagMask,
                clientData,
                getPasskeyNonce(),
                clientChallengeDataOffset
            );
    }

    function decodeEncodedInputs(
        bytes memory _inputs
    )
        public
        pure
        returns (bytes32 ,string memory, bytes memory, bytes1, bytes memory, uint)
    {
        (
            bytes32 pubkeyHash,
            string memory credentialId,
            bytes memory authenticatorData,
            bytes1 authenticatorDataFlagMask,
            bytes memory clientData,
            uint clientChallengeDataOffset
        ) = abi.decode(_inputs, (bytes32, string, bytes, bytes1, bytes, uint));

        return (
            pubkeyHash,
            credentialId,
            authenticatorData,
            authenticatorDataFlagMask,
            clientData,
            clientChallengeDataOffset
        );
    }

    function computeMessage(
        bytes memory authenticatorData,
        bytes1 authenticatorDataFlagMask,
        bytes memory clientData,
        string memory clientChallenge,
        uint clientChallengeDataOffset
    ) public pure returns (bytes32) {
        if (
            (authenticatorData[32] & authenticatorDataFlagMask) !=
            authenticatorDataFlagMask
        ) {
            revert InvalidAuthenticatorData();
        }

        bytes memory challengeExtracted = new bytes(
            bytes(clientChallenge).length
        );

        copyBytes(
            clientData,
            clientChallengeDataOffset,
            challengeExtracted.length,
            challengeExtracted,
            0
        );

        if (
            keccak256(abi.encodePacked(bytes(clientChallenge))) !=
            keccak256(abi.encodePacked(challengeExtracted))
        ) {
            revert InvalidClientData();
        }

        bytes memory verifyData = new bytes(authenticatorData.length + 32);

        copyBytes(
            authenticatorData,
            0,
            authenticatorData.length,
            verifyData,
            0
        );

        copyBytes(
            abi.encodePacked(sha256(clientData)),
            0,
            32,
            verifyData,
            authenticatorData.length
        );

        return (sha256(verifyData));
    }

    function copyBytes(
        bytes memory _from,
        uint _fromOffset,
        uint _length,
        bytes memory _to,
        uint _toOffset
    ) internal pure returns (bytes memory _copiedBytes) {
        uint minLength = _length + _toOffset;
        require(_to.length >= minLength);
        uint i = 32 + _fromOffset; 
        uint j = 32 + _toOffset;
        while (i < (32 + _fromOffset + _length)) {
            assembly {
                let tmp := mload(add(_from, i))
                mstore(add(_to, j), tmp)
            }
            i += 32;
            j += 32;
        }
        return _to;
    }
}","keccak256":"0xdab239a3b49e8bf403d2b0b3174862aa6811a3a02310584694eaaf50bfc629f5","license":"MIT"},"contracts/base/RecoveryManager.sol":{"content":"//SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "../verifier/RecoveryVerifier.sol";
import "../util/Conversion.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

contract RecoveryManager {
    using ECDSA for bytes32;

    RecoveryUltraVerifier public recoveryVerifier;

    bytes32 private recoveryKeyHash;

    uint256 private recoveryNonce;

    function _initializeRecoveryManager(address _recoveryVerifier, bytes32 _recoveryKeyHash) internal {
        recoveryVerifier = RecoveryUltraVerifier(_recoveryVerifier);
        recoveryKeyHash = _recoveryKeyHash;
    }

    function getRecoveryNonce() public view returns (string memory) {
        return Strings.toString(recoveryNonce);
    }

    function _useRecoveryNonce() internal returns (string memory) {
        recoveryNonce++;
        return Strings.toString(recoveryNonce);
    }

    function hashMessage(string memory message) public pure returns (bytes32) {
        string memory messagePrefix = "\x19Ethereum Signed Message:\n";

        string memory lengthString = Strings.toString(bytes(message).length);

        string memory concatenatedMessage = string(abi.encodePacked(messagePrefix, lengthString, message));

        return keccak256(bytes(concatenatedMessage));
    }

    function changeRecoveryVerifier(address _recoveryVerifier) internal {
        recoveryVerifier = RecoveryUltraVerifier(_recoveryVerifier);
    }

    function verifyRecovery(bytes calldata proof) public view returns (bool) {
        bytes32 message = hashMessage(getRecoveryNonce());

        bytes32[] memory _inputs = Conversion.convertInputs(message, recoveryKeyHash);

        return recoveryVerifier.verify(proof, _inputs);
    }

    function useRecovery(bytes calldata proof) internal returns (bool) {
        require(verifyRecovery(proof), "Invalid recovery");
        _useRecoveryNonce();
        return true;
    }

}","keccak256":"0x2adb3a9e24378a74b605b28807c05a159019da2e200ee7eabe01a7760f0e73a8","license":"MIT"},"contracts/callback/TokenCallbackHandler.sol":{"content":"// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.12;

